INSERT INTO "public"."workflow_block_types" ("id", "display_name", "description", "icon", "color_hex", "type", "enabled", "input_schema", "output_schema", "created_at", "updated_at") VALUES ('BlogGenerator', 'Blog Generator', 'Generates a full blog post from provided data sources.', 'file-pen-line', '#60a5fa', 'action', 'true', '{"type":"object","properties":{"query":{"type":"string","source":[{"block":"QueryText","output":"query"},{"block":"QueryOptimizer","output":"query"}],"description":"Optional topic or query to generate a blog post about"},"reddit_data":{"type":"array","source":[{"block":"RedditSearch","output":"reddit_data"}]},"web_search_data":{"type":"object","source":[{"block":"GoogleSearch","output":"web_search_data"}]},"writing_style_guide":{"type":"string","source":[{"block":"WritingStyleGuide","output":"writing_style_guide"}],"description":"Writing style guide to format the blog post"}}}', '{"type":"object","required":["blog_post"],"properties":{"blog_post":{"type":"object"}}}', '2025-06-22 11:43:24.322457+00', '2025-06-22 11:43:24.322457+00'), ('CompetitorAnalyzer', 'Competitor Analyzer', 'Analyzes competitor content from search results.', 'bar-chart-3', '#fb923c', 'action', 'true', '{"type":"object","properties":{"search_results":{"type":"object","source":[{"block":"GoogleSearch","output":"web_search_data"}]}}}', '{"type":"object","properties":{"analysis":{"type":"string","description":"Analysis of competitor content"}}}', '2025-06-22 11:43:24.322457+00', '2025-06-22 11:43:24.322457+00'), ('CTAInserter', 'CTA Inserter', 'Inserts a call-to-action into the blog post.', 'mouse-pointer-click', '#7dd3fc', 'action', 'true', '{"type":"object","properties":{"cta_text":{"type":"string"},"blog_body":{"type":"string","source":[{"block":"BlogGenerator","output":"body"}]}}}', '{"type":"object","properties":{"updated_body":{"type":"string"}}}', '2025-06-22 11:43:24.322457+00', '2025-06-22 11:43:24.322457+00'), ('GoogleSearch', 'Google Search', 'Performs a web search to find factual information and trends.', 'search', '#facc15', 'action', 'true', '{"type":"object","properties":{"query":{"type":"string","source":[{"block":"QueryText","output":"query"},{"block":"QueryOptimizer","output":"web_search_query"}],"description":"The query to search for"}}}', '{"type":"object","properties":{"web_search_data":{"type":"object","description":"Results from the web search"}}}', '2025-06-22 11:43:24.322457+00', '2025-06-22 11:43:24.322457+00'), ('ImageGenerator', 'Image Generator', 'Generates a relevant header image for a blog post.', 'image', '#c084fc', 'action', 'true', '{"type":"object","required":["list_of_prompts"],"properties":{"size":{"enum":["1024x1024","1024x1792","1792x1024"],"type":"string","source":"static","default":"1024x1024","description":"The dimensions of the generated images."},"list_of_prompts":{"type":"array","items":{"type":"string"},"source":[{"block":"ImagePromptGenerator","output":"list_of_prompts"}],"description":"A list of prompts to generate images from."}}}', '{"type":"object","required":["image_urls"],"properties":{"image_urls":{"type":"array","items":{"type":"object","properties":{"url":{"type":"string"},"path":{"type":"string"},"width":{"type":"integer"},"height":{"type":"integer"},"alt_text":{"type":"string"}}},"description":"A list of generated image objects, each with its path, public URL, dimensions, and alt text."}}}', '2025-06-22 11:43:24.322457+00', '2025-06-22 11:43:24.322457+00'), ('ImagePromptGenerator', 'Image Prompt Generator', 'Intelligently combines style guides and content to create an optimized DALL-E prompt.', 'sparkles', '#fde047', 'action', 'true', '{"type":"object","required":["blog_post","image_style"],"properties":{"blog_post":{"type":"object","source":[{"block":"BlogGenerator","output":"blog_post"}],"description":"The full blog post object from the generator node."},"num_images":{"type":"integer","source":"static","description":"Optional number of images to generate. If not provided, the system will decide."},"image_style":{"type":"string","source":[{"block":"WritingStyleGuide","output":"image_style"}],"description":"The style guide to use for generating the images."}}}', '{"type":"object","required":["list_of_prompts"],"properties":{"list_of_prompts":{"type":"array","items":{"type":"string"},"description":"A list of generated image prompts."}}}', '2025-06-25 16:27:14.394542+00', '2025-06-25 16:27:14.394542+00'), ('InternalLinkSuggester', 'Internal Link Suggester', 'Recommend internal links for SEO (coming soon)', 'TbLink', '#f472b6', 'action', 'false', '{}', '{}', '2025-06-22 17:37:38.14162+00', '2025-06-22 17:37:38.14162+00'), ('KeywordClusterer', 'Keyword Clusterer', 'Group related keywords into clusters (coming soon)', 'TbStack', '#7dd3fc', 'action', 'false', '{}', '{}', '2025-06-22 17:37:38.14162+00', '2025-06-22 17:37:38.14162+00'), ('QueryOptimizer', 'Optimize Query', 'Uses an LLM to optimize a query for different search targets (e.g., Reddit vs. Web).', 'Sparkles', '#4A90E2', 'action', 'true', '{"type":"object","required":["query"],"properties":{"query":{"type":"string","source":[{"block":"QueryText","output":"query"}],"description":"The user''s initial query or topic"}}}', '{"type":"object","required":["reddit_query","web_search_query"],"properties":{"reddit_query":{"type":"string"},"web_search_query":{"type":"string"}}}', '2025-06-22 17:37:38.14162+00', '2025-06-22 17:37:38.14162+00'), ('QueryText', 'Query Text', 'Provides a static text query to start a workflow.', 'file-text', '#4ade80', 'data', 'true', '{"type":"object","required":["query"],"properties":{"query":{"type":"string","description":"The search query or topic"}}}', '{"type":"object","properties":{"query":{"type":"string","description":"The output query"}}}', '2025-06-22 11:43:24.322457+00', '2025-06-22 11:43:24.322457+00'), ('RedditSearch', 'Reddit Search', 'Searches Reddit to find community discussions and opinions.', 'message-circle', '#f87171', 'action', 'true', '{"type":"object","properties":{"query":{"type":"string","source":[{"block":"QueryText","output":"query"},{"block":"QueryOptimizer","output":"reddit_query"}],"description":"The query to search for on Reddit"}}}', '{"type":"object","properties":{"reddit_data":{"type":"array","description":"A list of discussions from Reddit"}}}', '2025-06-22 11:43:24.322457+00', '2025-06-22 11:43:24.322457+00'), ('SaveBlogPost', 'Save Blog Post', 'Saves the generated blog content to the database.', 'Save', '#10b981', 'action', 'true', '{"type":"object","required":["blog_post"],"properties":{"blog_post":{"type":"object","source":[{"block":"BlogGenerator","output":"blog_post"}],"description":"The generated text content for the blog post."},"image_urls":{"type":"array","source":[{"block":"ImageGenerator","output":"image_urls"}],"description":"The metadata of the generated images (optional)."}}}', '{"type":"object","required":["saved_blog_post"],"properties":{"saved_blog_post":{"type":"object","description":"The final, saved blog post object from the database."}}}', '2025-06-22 17:37:38.14162+00', '2025-06-22 17:37:38.14162+00'), ('TrendingTopics', 'Trending Topics', 'Fetch current trending topics from news API', 'TbTrendingUp', '#f472b6', 'data', 'false', '{"type":"object","properties":{}}', '{"type":"object","properties":{"topics":{"type":"array","items":{"type":"string"}}}}', '2025-06-22 17:37:38.14162+00', '2025-06-22 17:37:38.14162+00'), ('TwitterAPI', 'Twitter API', 'Fetches a public tweet and returns its text, images and outbound links.', 'brand-twitter', '#1DA1F2', 'data', 'true', '{"type":"object","required":["tweet_url"],"properties":{"tweet_url":{"type":"string","title":"Tweet URL","format":"uri","source":"static"}}}', '{"type":"object","properties":{"url":{"type":"string","format":"uri"},"urls":{"type":"array","items":{"type":"string","format":"uri"}},"content":{"type":"string"},"image_urls":{"type":"array","items":{"type":"string","format":"uri"}}}}', '2025-06-23 08:14:16.557901+00', '2025-06-23 08:14:16.557901+00'), ('WritingStyleGuide', 'Writing Style', 'Select a pre-defined writing style guide to instruct the blog generator.', 'BookText', '#a855f7', 'data', 'true', '{}', '{"type":"object","properties":{"writing_style_guide":{"type":"string"}}}', '2025-06-23 15:54:00.78702+00', '2025-06-23 15:54:00.78702+00');